function hw04_student()%Keenan Parker 1001024878%part (a) blurring images with a lowpass filteroriginal_clock = imread('clock-5.1.12.tiff');single_clock = single(original_clock);sevenPointAvg = [1/7 1/7 1/7 1/7 1/7 1/7 1/7];blurred_clock_h = conv2(sevenPointAvg,single_clock);blurred_clock = conv2(sevenPointAvg',blurred_clock_h);figure(1)imshow(uint8(original_clock));title('Original clock');figure(2)imshow(uint8(blurred_clock));title('Blurred clock');%part (b)Sharpening images with a highpass filterfilter_b = [-1 3 -1];sharp_clock_h = conv2(filter_b,single_clock);sharp_clock = conv2(filter_b',sharp_clock_h);figure(3)imshow(uint8(sharp_clock));title('Sharpened clock');%part (c)Edge detection with a highpass filterfilter_c = [1 -1];edge_detection_h = conv2(filter_c,single_clock);edge_detection = conv2(filter_c',edge_detection_h);edge_detection = 127 - edge_detection;figure(4)imshow(uint8(edge_detection));title('Edge detection clock');%part (d)original_man = imread('man-5.3.01.tiff');figure(5)imshow(uint8(original_man));title('Original man');noisyMan = imnoise(original_man, 'salt & pepper', 0.2);figure(6)imshow(uint8(noisyMan));title('Noisy man');cleanMan =  medfilt2(noisyMan, [5, 5]);figure(7)imshow(uint8(cleanMan));title('Clean man');end